import React from 'react'

class Home extends React.Component{
    constructor(){
        super()
        this.state = {
                userImg: "",
                userBio: "",
                username: "",
                followed_users: [],
                followed_shouts: [],
                unfollowed_users: [],
        }
    }

    componentDidMount(){
        const token = localStorage.getItem('token')
        const reqObj = {
            method: 'GET',
            headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}
        }
        fetch('http://localhost:3000/current_user', reqObj)
        .then(resp => resp.json())
        .then(data => 
            // console.log(data)
               { this.setState({
                   userImg: data.imgUrl, username: data.username, userBio: data.bio, followed_users: data.followed_users, followed_shouts: data.followed_shouts
               })}
            )

    }

    render(){
        console.log(this.state)
        return(
            <div>
                <div>
                    <h1>Home</h1>
                    <div>NavBar</div>
                    <div>Header: picture and bio</div>
                </div>



                <div>ShoutOuts/newsFeed</div>
                <div>Find a new Follow</div>
                <div>Create Shout</div>

            </div>
        )
    }
}

export default Home
