import React from 'react'
import NavBar from './NavBar'
import Findfollow from './Findfollow'
import Shoutform from './Shoutform'
import Newsfeed from '/Newsfeed'

class Home extends React.Component{
    constructor(){
        super()
        this.state = {
                userImg: "",
                userBio: "",
                username: "",
                followed_users: [],
                followed_shouts: [],
                unfollowed_users: [],
        }
    }

    componentDidMount(){
        const token = localStorage.getItem('token')
        const reqObj = {
            method: 'GET',
            headers: {'Content-Type': 'application/json', 'Authorization': `Bearer ${token}`}
        }
        fetch('http://localhost:3000/current_user', reqObj)
        .then(resp => resp.json())
        .then(data => 
               { this.setState({
                   userImg: data.imgUrl, username: data.username, userBio: data.bio, followed_users: data.followed_users, followed_shouts: data.followed_shouts
               })}
            )

    }

    render(){

        return(
            <div>
                <NavBar/>
                <div>
                    <h1>Home</h1>

                    <div>Header: picture and bio</div>
                </div>



                <div>ShoutOuts/newsFeed</div>
                <div>Find Follow</div>
                <div>Create Shout</div>

            </div>
        )
    }
}

export default Home
