import React from 'react'


class Login extends React.Component{

    constructor(){
        super()
        this.state = {
            username: "",
            password: ""
        }
    }

    handleInputChange = (event) => {
        this.setState({
           [event.target.name]: event.target.value
        })
    }

    handleLogin = (event) => {
        event.preventDefault()
        // console.log(this.state)
        const token = localStorage.getItem('token')
        const reqObj = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.state)
        }

        fetch('http://localhost:3000/login', reqObj)
        .then(resp => resp.json())
        .then(data => {
            console.log(data)
            // recieve data back from login user 
            // set token to local storage
            // redirect to the user profile with that data 

            if(data.error){
                alert(data.error)}
                else{
                        // console.log(data)
                        localStorage.setItem('token', data.token)
                        this.props.history.push('/home')
                }
            }
        )
    }

    goSignUp = () => {
        this.props.history.push('/signup')
    }


    render(){
        return(
            <div>
                <h1>Login</h1>
                <button onClick={this.goSignUp}>Signup</button>
                <div className="form_container">
                    <form action="">
                        <input type="username" name="username" onChange={ (event) => {this.handleInputChange(event)}}id="" placeholder="username"/>


                        <input type="password" name="password" onChange={ (event) => {this.handleInputChange(event)}}id="" placeholder="password"/>

                        <input type="submit" value="Login"/>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login
