import React from 'react'

class Signup extends React.Component{
    constructor(){
        super()
        this.state = {

        }
    }

    handleForm = (event) => {
        event.preventDefault()
        // console.log(this.state)

        const loginObj = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.state)
        }

        fetch('http://localhost:3000/signup', loginObj)
        .then(resp => resp.json())
        .then(data => {
            if(data.error){alert(data.error)}

            else { localStorage.setItem('token', data.token)
                this.props.history.push('/home')
                } 
            }
        ) 
    }


    render(){
        return(
            <div>
                <div className='form-container'>
                    <form action="" method="post">

                    <label htmlFor="first_name">Full name</label>

                <input onChange={(event) => {this.handleInputChange(event)}}type="text" name="firsr_name" id="" placeholder="John"/>


                <label htmlFor="last_name">Full name</label>

                <input onChange={(event) => {this.handleInputChange(event)}}type="text" name="last_name" id="" placeholder="Smith"/>

                        <label htmlFor="username">Username </label>
                        <input onChange={(event) => {this.handleInputChange(event)}} 
                    type="text" name="username" id=""/>

                        <br/>

                         <label htmlFor="password">Password </label>
                        <input onChange={(event) => {this.handleInputChange(event)}}type="password" name="password" id=""/>

                        <br/>

                        <label htmlFor="bio">Biography </label>
                        <textarea onChange= {(event) => this.props.handleInputChange(event)} name="bio" id="" cols="30" rows="10" maxLength='250'></textarea>





                    </form>
                </div>
            </div>
        )
    }
}

export default Signup
